INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Boiled eggs ', 'Non-Veg', 'Global', 'Egg, Water, Salt  ', '2 eggs', 20, 10, 'Take 2 eggs and rinse them under water;In a saucepan, add enough water to fully submerge the eggs;Add a pinch of salt to prevent cracking;Bring the water to a boil and then let the eggs boil for 8-10 minutes;Remove from heat and transfer eggs to cold water for easy peeling  ', 'ChatGPT', '001.jpg', 'Snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Masala Chai', 'Veg', 'Indian', 'Water, Milk, Tea Leaves, Ginger,Cardamom,Clove,Sugar  ', '2 cups', 15, 10, 'Boil 1 cup of water in a saucepan;Add crushed ginger (1/2 inch), 2 crushed cardamom pods, and 1 clove;Let it boil for 2 minutes to release the flavors;Add 2 tsp tea leaves and boil for another minute;Pour in 1 cup milk and add 2 tsp sugar (or to taste);Simmer for 3-4 minutes on low heat;Strain into cups and serve hot                                                                                                                                                                         ', 'ChatGPT', '002.jpg', 'Beverage');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('White sauce pasta', 'Veg', 'Italian ', 'Pasta, Butter, Maida, Milk, Cheese, Oregano, Chili Flakes, Garlic, Black Pepper, Salt, Vegetables  ', '1 plate', 80, 25, 'Boil 100g pasta in salted water until just cooked and slightly firm;Drain the pasta and toss with a little oil to prevent sticking;In a pan, melt 1 tablespoon butter, then add 1 tablespoon maida (all-purpose flour) and cook on low flame for 1-2 minutes until it smells slightly nutty (do not brown it);Gradually pour in 200 ml milk, stirring continuously to avoid lumps;Add 1/4 teaspoon black pepper, 1/4 teaspoon chili flakes, 1/4 teaspoon mixed herbs, and salt. Stir and cook for 2-3 minutes until the sauce thickens;Optional: Add 2 tablespoons grated cheese and stir until melted;Add the boiled pasta, 1/4 cup chopped onions, 1/4 cup chopped capsicum, and 1/4 cup boiled sweet corn;Mix everything well, simmer for 2 minutes, then turn off the heat;Serve hot with optional herbs or extra cheese on top. ', 'ChatGPT', '003.jpg', 'Main Course');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('French Toast', 'Non-Veg', 'French  ', 'Bread, egg, sugar, cinnamon powder, vanilla essence, butter  ', '2 slices', 40, 15, 'Crack an egg into a bowl and whisk it;Add 50 ml milk, 1 tsp sugar, 1/4 tsp cinnamon powder, and 1/4 tsp vanilla essence to the bowl and mix well;Dip each bread slice into the mixture, coating both sides evenly;Heat 1 tsp butter on a pan over medium heat;Cook the coated bread slices until golden brown on both sides;Serve hot with honey, fruits, or maple syrup  ', 'ChatGPT', '004.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Virgin mojito', 'Veg', 'Continental ', 'Mint leaves, Lemon wedges, Sugar, Soda water, Chilled water, Ice cubes  ', '2 glasses', 40, 10, 'Take 10-12 fresh mint leaves and 4 lemon wedges in a sturdy glass; Add 2 teaspoons of sugar; Muddle the mint and lemon gently to release flavors (do not over crush); Fill the glass halfway with ice cubes; Pour in 1/2 cup chilled water and top up with soda water; Stir gently to combine all ingredients; Garnish with a mint sprig and a lemon slice before serving  ', 'ChatGPT', '005.jpg', 'Beverage');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Paneer Butter Masala', 'Veg', 'Punjabi', 'coriander, ginger, garlic, chicken, cloves', '1 bowl', 167, 58, 'Heat 2 tablespoons of butter in a pan and sauté 1 finely chopped onion until golden;Add 1 teaspoon ginger-garlic paste and cook for a minute until fragrant;Add 3 chopped tomatoes and cook until soft;Let it cool slightly and blend into a smooth puree;In the same pan, melt 1 tablespoon butter and pour in the tomato puree;Add 1/2 teaspoon turmeric, 1 teaspoon red chili powder, 1 teaspoon coriander powder, and salt to taste;Cook the masala for 57 minutes until it thickens and releases oil;Add 1/2 cup water or cream and stir well;Add 200 grams paneer cubes and simmer for 34 minutes;Sprinkle 1 teaspoon kasuri methi and 1/2 teaspoon garam masala;Add 1 tablespoon fresh cream and mix gently;Garnish with chopped coriander and serve hot with naan or rice.', 'ChatGPT', '006.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chicken Biryani', 'Non-Veg', 'Hyderabadi', 'cloves, mint, cardamom, milk, yogurt', '1 bowl', 198, 52, 'Marinate 500g chicken with 1 cup yogurt, 1 tsp turmeric, 2 tsp red chili powder, 1 tsp garam masala, salt, and juice of 1 lemon; Let it rest for 1 hour; In a large pot, heat 2 tbsp ghee and sauté 2 sliced onions until golden brown; Add 1 tbsp ginger-garlic paste and cook for 2 minutes; Add marinated chicken and cook until sealed; Add 2 chopped tomatoes and cook until soft; Add 1/4 cup water and simmer for 10 minutes; In another pot, boil 2 cups basmati rice with whole spices (bay leaf, cloves, cardamom) until 70% cooked; Layer rice over chicken, sprinkle with fried onions, mint, coriander, and saffron milk; Cover tightly and cook on low flame for 20 minutes; Serve hot with raita and salad.', 'ChatGPT', '007.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Masala Dosa', 'Veg', 'South Indian', 'pasta, chicken, lemon, yogurt, chili powder', '1 plate', 251, 83, 'Soak 1 cup rice and 1/2 cup urad dal for 46 hours; Grind to a smooth batter and ferment overnight; Boil and mash 3 potatoes; In a pan, heat 1 tbsp oil, add mustard seeds, curry leaves, chopped onion, green chili, and turmeric; Add mashed potatoes and salt, mix well to prepare filling; Heat dosa tawa, pour a ladle of batter and spread thin; Drizzle oil and cook until golden; Place potato masala inside and fold the dosa; Serve hot with coconut chutney and sambar.', 'ChatGPT', '008.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Mango Shake', 'Veg', 'Indian', 'sugar, onion, ginger, garlic, coriander', '1 glass', 67, 88, 'Peel and chop 2 ripe mangoes; Blend with 2 cups chilled milk, 2 tbsp sugar, and a few ice cubes until smooth; Pour into a glass and top with chopped mango pieces or dry fruits; Serve chilled in a tall glass.', 'ChatGPT', '009.jpg', 'shakes');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Veg Momos', 'Veg', 'Tibetan', 'onion, garlic, coriander, pasta, turmeric', '8 pieces', 259, 55, 'Finely chop cabbage, carrot, and capsicum; Sauté in 1 tsp oil with garlic, pepper, and soy sauce; Let it cool; Prepare dough with all-purpose flour and water; Roll into small discs, fill with vegetable mix, and shape momos; Steam for 1012 minutes until translucent.', 'ChatGPT', '010.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Egg Curry', 'Non-Veg', 'Bengali', 'lemon, salt, pasta, mint, chicken', '1 bowl', 176, 41, 'Boil 4 eggs and peel; In a pan, heat 2 tbsp oil, add cumin, chopped onion, and sauté until golden; Add 1 tsp ginger-garlic paste and cook; Add 2 chopped tomatoes, turmeric, chili powder, coriander powder, and salt; Cook to a thick gravy; Add water, bring to boil, add eggs, and simmer for 10 minutes.', 'ChatGPT', '011.jpg', 'breakfast');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Pav Bhaji', 'Veg', 'Maharashtrian', 'cloves, lemon, ghee, garlic, onion', '5 pav, 1 pan bhaji', 244, 75, 'Boil and mash 2 potatoes, 1 cup cauliflower, and 1/2 cup peas; In a pan, heat 2 tbsp butter, sauté onions, ginger-garlic paste, and chopped tomatoes; Add turmeric, chili powder, pav bhaji masala, and salt; Add mashed vegetables and cook well; Add water as needed; Toast 4 pav with butter; Serve 4 pav with 1 pan of bhaji, garnished with onion and lemon.', 'ChatGPT', '012.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chole Bhature', 'Veg', 'Punjabi', 'lemon, paneer, ginger, ghee, cloves', '1 bowl chole, 2 bhature', 246, 21, 'Soak and pressure cook 1 cup chickpeas; In a pan, heat oil, add cumin, chopped onion, ginger-garlic paste, and tomato puree; Add chole masala, chili powder, turmeric, and salt; Add chickpeas and cook for 15 minutes; For bhature, make dough using flour, yogurt, and baking soda; Rest for 2 hours; Roll and deep fry; Serve with 2 bhature and 1 bowl of chole.', 'ChatGPT', '013.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Aloo Tikki', 'Veg', 'North Indian', 'tomato, chicken, milk, yogurt, ginger', '2 pieces', 85, 28, 'Boil and mash 3 potatoes; Mix with spices, breadcrumbs, and chopped coriander; Shape into tikkis; Shallow fry till golden; Serve 23 pieces with chutneys.', 'ChatGPT', '014.jpg', 'breakfast');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Lassi', 'Veg', 'Punjabi', 'sugar, chicken, coriander, salt, lemon', '1 glass', 228, 36, 'Blend 1 cup curd with 1/2 cup water, 2 tbsp sugar, and a pinch of cardamom; Serve chilled in a glass topped with malai or chopped nuts.', 'ChatGPT', '015.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Tandoori Chicken', 'Non-Veg', 'Punjabi', 'rice, milk, chili powder, sugar, garlic', '1 bowl', 190, 81, 'Marinate 4 chicken legs with yogurt, lemon juice, ginger-garlic paste, and spices; Rest for 4 hours; Roast in oven or grill until cooked and charred; Serve hot with onion rings and lemon wedges.', 'ChatGPT', '016.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Veg Pulao', 'Veg', 'North Indian', 'coriander, ghee, chicken, milk, cardamom', '1 bowl', 86, 14, 'Rinse and soak 1 cup basmati rice for 20 minutes; Heat 2 tbsp oil in a pan, add whole spices (bay leaf, cinnamon, cloves), 1 sliced onion, and sauté until golden; Add 1 tsp ginger-garlic paste and cook for a minute; Add 1/2 cup chopped vegetables like carrot, beans, and peas; Sauté for 23 minutes, then add soaked rice and 2 cups water; Add salt and a pinch of garam masala; Cover and cook on low flame for 15 minutes or until rice is done; Serve hot with raita and salad.', 'ChatGPT', '017.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Butter Naan', 'Veg', 'North Indian', 'turmeric, chicken, cloves, pasta, garlic', '1 plate', 251, 45, 'In a bowl, mix 2 cups all-purpose flour, 1/2 tsp salt, 1/2 tsp sugar, 1/2 tsp baking powder, 2 tbsp yogurt, and water to form a soft dough; Cover and rest for 2 hours; Divide dough into balls, roll into oval shapes; Heat tawa, place naan and cook until bubbles form; Flip and cook other side, pressing gently; Brush with butter and optional garlic; Serve hot with paneer or curry.', 'ChatGPT', '018.jpg', 'dinner');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Sambar', 'Veg', 'South Indian', 'salt, paneer, chili powder, cloves, onion', '1 bowl', 242, 65, 'Soak 1/2 cup toor dal for 30 minutes, pressure cook with turmeric until soft; Mash and keep aside; In a pan, heat 1 tbsp oil, sauté mustard seeds, curry leaves, 1 chopped onion, 1 chopped tomato, and chopped vegetables like drumstick or carrot; Add 2 cups water, salt, and cook until veggies are soft; Add cooked dal, 2 tbsp tamarind pulp, and 2 tsp sambar powder; Simmer for 10 minutes; Prepare a tempering with mustard seeds, red chilies, and curry leaves in ghee and pour over sambar; Serve hot with idli, dosa, or rice.', 'ChatGPT', '019.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Dhokla', 'Veg', 'Gujarati', 'pasta, ginger, cardamom, paneer, chicken', '1 plate', 73, 21, 'In a bowl, mix 1 cup besan, 1 tbsp semolina, 1/2 tsp turmeric, 1 tsp sugar, salt, and water to make a smooth batter; Add 1 tsp Eno fruit salt just before steaming; Pour into greased plate and steam for 15 minutes; For tempering, heat 1 tbsp oil, add mustard seeds, curry leaves, green chilies, and 2 tbsp water; Pour over steamed dhokla; Cut into squares; Serve warm with green chutney.', 'ChatGPT', '020.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Cold Coffee', 'Veg', 'Western', 'sugar, paneer, mint, turmeric, onion', '1 glass', 251, 47, 'In a blender, add 2 cups cold milk, 2 tbsp instant coffee, 2 tbsp sugar, and a few ice cubes; Blend until smooth and frothy; Drizzle chocolate syrup inside a glass (optional) and pour coffee over; Serve chilled with ice cubes or whipped cream on top.', 'ChatGPT', '021.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Fruit Punch', 'Veg', 'Western', 'cloves, garlic, cardamom, ginger, coriander', '1 glass', 224, 67, 'Chop 1/2 cup mixed fruits like apple, banana, pineapple, and grapes into small pieces; In a bowl, mix 1 cup orange juice, 1/2 cup pineapple juice, and 1/2 cup soda or lemon juice; Add fruits and 1 tbsp sugar if needed; Chill for 30 minutes; Pour into glasses and garnish with mint; Serve cold with a straw.', 'ChatGPT', '022.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Paneer Tikka', 'Veg', 'Punjabi', 'yogurt, rice, paneer, tomato, chili powder', '1 bowl', 65, 12, 'Cut 200g paneer into cubes; Prepare marinade using 1/2 cup yogurt, 1 tsp red chili powder, 1/2 tsp turmeric, 1 tsp garam masala, 1 tsp lemon juice, and salt; Mix in paneer, onion, and capsicum pieces; Let it rest for 30 minutes; Skewer the pieces and grill or roast in oven at 200°C for 15 minutes, turning once; Baste with butter midway; Serve hot with mint chutney and lemon wedges.', 'ChatGPT', '023.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chicken Korma', 'Non-Veg', 'Mughlai', 'garlic, mint, paneer, chili powder, cloves', '1 bowl', 207, 38, 'Heat 2 tbsp oil in a pan, add 2 chopped onions and sauté until golden; Add 1 tbsp ginger-garlic paste and cook for 2 minutes; Add 500g chicken, 1 tsp turmeric, 1 tsp chili powder, and salt; Sauté for 56 minutes; Add 1/2 cup yogurt and mix well; Add 1/4 cup water and simmer covered for 20 minutes; Add 1 tbsp cashew paste and cook for 5 more minutes until thick; Serve hot with naan or rice and garnish with fried onions.', 'ChatGPT', '024.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Rajma Chawal', 'Veg', 'North Indian', 'cloves, paneer, yogurt, turmeric, chili powder', '1 plate', 188, 18, 'Soak 1 cup rajma (kidney beans) overnight and pressure cook until soft; In a pan, heat 2 tbsp oil, sauté 1 chopped onion, 1 tsp ginger-garlic paste, and 2 chopped tomatoes; Add 1 tsp turmeric, 1 tsp chili powder, 1 tsp garam masala, and salt; Add cooked rajma and 1 cup water, simmer for 1520 minutes; Mash some beans for thickness; Cook 1 cup rice separately; Serve hot with 1 bowl rajma and 1 plate rice, topped with chopped coriander.', 'ChatGPT', '025.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Gulab Jamun', 'Veg', 'North Indian', 'lemon, ghee, salt, rice, turmeric', '5 pieces', 112, 19, 'Mix 1 cup khoya, 2 tbsp all-purpose flour, and a pinch of baking soda; Knead into soft dough and shape into small balls; Heat oil or ghee on low and fry until golden brown; In a pan, boil 1 cup sugar with 1 cup water and 2 crushed cardamom pods to make syrup; Add fried jamuns to hot syrup and soak for 1 hour; Serve warm with syrup and garnish with chopped pistachios.', 'ChatGPT', '026.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Jalebi', 'Veg', 'North Indian', 'rice, cardamom, mint, ghee, milk', '8 pieces', 111, 78, 'In a bowl, mix 1 cup all-purpose flour, 1 tbsp cornflour, 1/2 tsp baking soda, and 1/2 cup yogurt; Add water to make a smooth batter and let it ferment for 68 hours; Prepare sugar syrup by boiling 1 cup sugar with 1/2 cup water, cardamom powder, and a few saffron strands until slightly sticky; Fill batter in a piping bag or bottle and pipe spirals into hot oil; Fry on low heat until crisp and golden; Dip hot jalebis in warm sugar syrup for a few seconds and remove; Serve warm with rabri or as is.', 'ChatGPT', '027.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Idli', 'Veg', 'South Indian', 'milk, lemon, tomato, pasta, ginger', '10-12 piece', 61, 68, 'Rinse and soak 1 cup rice and 1/2 cup urad dal separately for 4 hours; Grind them into a smooth batter and mix together with a little salt; Ferment overnight or for 8 hours until doubled in volume; Grease idli molds and pour the batter; Steam for 1012 minutes until a toothpick comes out clean; Remove from molds and serve hot with sambar and coconut chutney.', 'ChatGPT', '028.jpg', 'breakfast');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Rasgulla', 'Veg', 'Bengali', 'paneer, milk, tomato, ghee, turmeric', '8 pieces', 99, 81, 'Boil 1 liter milk and curdle with 2 tbsp lemon juice; Strain using muslin cloth and wash the chenna under cold water to remove lemony taste; Knead the chenna for 810 minutes until smooth; Roll into small balls; In a pressure cooker, boil 1 cup sugar with 4 cups water and cardamom; Add balls and cook for 78 minutes on medium flame; Let cool for 1 hour; Serve chilled soaked in syrup.', 'ChatGPT', '029.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Spring Roll', 'Veg', 'Chinese', 'cloves, ginger, salt, yogurt, mint', '4 pieces', 295, 36, 'In a pan, heat 1 tbsp oil and sauté 1/4 cup chopped onion, 1/2 cup shredded cabbage, 1/4 cup grated carrot, and 1/4 cup capsicum; Add 1 tbsp soy sauce, salt, and pepper; Stir-fry for 34 minutes and let it cool; Place filling on spring roll sheets, fold and roll tightly sealing edges with slurry; Deep-fry in hot oil until golden and crisp; Serve hot with chili sauce or ketchup.', 'ChatGPT', '030.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Hakka Noodles', 'Veg', 'Chinese', 'onion, ghee, ginger, chili powder, yogurt', '1 bowl', 66, 78, 'Boil 150g noodles until al dente, drain and toss with oil; In a wok, heat 2 tbsp oil, add chopped garlic, 1 sliced onion, 1/4 cup capsicum, 1/4 cup cabbage, and 1/4 cup carrots; Stir-fry on high heat for 23 minutes; Add noodles, 1 tbsp soy sauce, 1 tsp vinegar, 1/2 tsp chili sauce, and salt; Toss everything well; Serve hot garnished with spring onions.', 'ChatGPT', '031.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Pani Puri', 'Veg', 'Mumbai', 'mint, lemon, garlic, coriander, pasta', '8 pieces', 148, 13, 'Boil 1/2 cup tamarind and strain to make pulp; Mix with 2 cups water, mint paste, 1/2 tsp chaat masala, black salt, and 1/2 tsp cumin powder; For stuffing, mix boiled mashed potatoes with chopped onion, coriander, and spices; Crack open puris, fill with stuffing and spicy water; Serve immediately for a crispy bite.', 'ChatGPT', '032.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Aam Panna', 'Veg', 'North Indian', 'sugar, coriander, pasta, turmeric, milk', '1 glass', 50, 76, 'Boil 2 raw mangoes until soft and peel the skin; Extract the pulp and blend with 1/2 cup sugar, 1/2 tsp roasted cumin powder, 1/4 tsp black salt, and 2 cups cold water; Stir well and refrigerate; Serve chilled with ice cubes and mint leaves.', 'ChatGPT', '033.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('jal jeera', 'Veg', 'North Indian', 'mint, cumin, black salt, lemon, tamarind', '1 glass', 6, 10, 'Grind mint leaves, 1 tsp roasted cumin seeds, and 1 tbsp tamarind pulp into a smooth paste; Mix the paste with 1 cup water, add 1 tsp black salt, 1/2 tsp regular salt, and 1 tbsp lemon juice; Stir well and strain the mixture; Chill in the fridge for 1520 minutes; Serve cold with a few boondi on top and mint leaves for garnish.', 'ChatGPT', '034.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Palak Paneer', 'Veg', 'North Indian', 'mint, chicken, yogurt, chili powder, garlic', '1 bowl', 110, 85, 'Blanch 2 cups spinach leaves in hot water for 2 minutes, then cool and blend to a smooth puree; Heat 2 tbsp oil, sauté 1 chopped onion and 1 tsp ginger-garlic paste; Add 1 chopped tomato and cook till soft; Add 1/2 tsp turmeric, 1 tsp chili powder, and salt; Add spinach puree and cook for 5 minutes; Add 200g paneer cubes and simmer for 5 more minutes; Finish with a splash of cream; Serve hot with naan or rice.', 'ChatGPT', '035.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Fish Curry', 'Non-Veg', 'Goan', 'yogurt, lemon, turmeric, ginger, tomato', '1 bowl', 169, 83, 'Clean 500g fish pieces and marinate with salt and turmeric; Heat 2 tbsp oil, add mustard seeds, curry leaves, 1 chopped onion, and sauté; Add 1 tsp ginger-garlic paste, 1 chopped tomato, 1 tsp chili powder, and 1/2 tsp coriander powder; Cook till masala leaves oil; Add 1 cup coconut milk and bring to a boil; Add fish and simmer for 10 minutes; Serve hot with steamed rice and lemon wedges.', 'ChatGPT', '036.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Upma', 'Veg', 'South Indian', 'rice, coriander, paneer, salt, cardamom', '1 plate', 182, 89, 'Dry roast 1 cup rava (semolina) in a pan for 34 minutes and set aside; Heat 2 tbsp oil or ghee, add mustard seeds, 1 tsp urad dal, chopped curry leaves, 1 chopped green chili, and 1 chopped onion; Sauté until the onion turns translucent; Add 2.5 cups water and salt to taste; When the water starts boiling, slowly add the roasted rava while stirring continuously to avoid lumps; Cook on low flame, stirring until the upma thickens and rava is fully cooked; Garnish with coriander and a squeeze of lemon; Serve hot with coconut chutney or pickle.', 'ChatGPT', '037.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Kadhi Pakora', 'Veg', 'Punjabi', 'chicken, paneer, tomato, garlic, yogurt', '1 plate', 66, 14, 'Mix 1 cup curd with 2 tbsp besan, 1/2 tsp turmeric, salt, and 2.5 cups water; In a pan, heat 1 tbsp oil, add mustard seeds, cumin, curry leaves, and 2 dried red chilies; Add the curd mixture and simmer on low heat for 1012 minutes, stirring occasionally; For pakoras, mix 1/2 cup besan, salt, chopped onion, and water to make a thick batter; Deep-fry spoonfuls of batter until golden; Add pakoras to the kadhi and simmer for 23 minutes; Serve hot with steamed rice.', 'ChatGPT', '038.jpg', 'lunch');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Aloo Paratha', 'Veg', 'Punjabi', 'cloves, garlic, chili powder, paneer, yogurt', '2 piece', 84, 82, 'Boil 2 large potatoes, peel and mash; Mix with 1 chopped green chili, chopped coriander, 1/2 tsp ajwain, salt, and 1/2 tsp garam masala; Knead 2 cups wheat flour with water to make dough and let it rest for 15 minutes; Take a small portion of dough, roll slightly, stuff with potato filling, and roll again into a paratha; Cook on a hot tawa with ghee until both sides are golden brown; Serve hot with butter, pickle, or curd.', 'ChatGPT', '039.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chicken 65', 'Non-Veg', 'South Indian', 'pasta, cardamom, cloves, mint, rice', '1 bowl', 209, 70, 'Marinate 300g chicken pieces with 2 tbsp curd, 1 tbsp ginger-garlic paste, 1 tsp red chili powder, 1/2 tsp turmeric, 1/2 tsp garam masala, salt, and 1 tbsp cornflour; Let it rest for 30 minutes; Deep-fry the marinated chicken until crispy and golden; In a pan, heat 1 tbsp oil, add curry leaves, 2 chopped green chilies, and sauté briefly; Toss in the fried chicken and cook for 23 minutes; Serve hot with lemon wedges and onion rings.', 'ChatGPT', '040.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Nimbu Pani', 'Veg', 'Indian', 'salt, coriander, yogurt, pasta, milk', '1 glass', 94, 63, 'In a glass, squeeze the juice of 1 lemon; Add 1 tbsp sugar, a pinch of salt, and 1/4 tsp roasted cumin powder; Fill the glass with chilled water and stir well; Add ice cubes and garnish with mint leaves; Serve immediately for a refreshing summer drink.', 'ChatGPT', '041.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Kheer', 'Veg', 'North Indian', 'pasta, milk, tomato, cardamom, chicken', '1 plate', 74, 28, 'Wash 1/4 cup basmati rice and soak for 30 minutes; Boil 1 liter of full cream milk in a pan and add drained rice; Cook on low flame, stirring occasionally, until rice softens and mixture thickens; Add 1/2 cup sugar, 1/4 tsp cardamom powder, and a few saffron strands; Simmer for another 5 minutes; Garnish with chopped almonds and pistachios; Serve warm or chilled as per preference.', 'ChatGPT', '042.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Moong Dal Halwa', 'Veg', 'Rajasthani', 'coriander, rice, garlic, chili powder, paneer', '1 plate', 278, 27, 'Soak 1 cup yellow moong dal for 45 hours and grind to a coarse paste; Heat 1/2 cup ghee in a heavy pan, add the paste, and stir continuously on low flame for 2025 minutes until golden and aromatic; In another pan, boil 1 cup sugar with 1.5 cups water and a pinch of cardamom powder to make syrup; Add syrup gradually to the halwa while stirring; Cook until it absorbs completely and ghee starts releasing; Garnish with chopped nuts and serve warm.', 'ChatGPT', '043.jpg', 'dessert');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Sabudana Khichdi', 'Veg', 'Maharashtrian', 'yogurt, sugar, chili powder, milk, cloves', '1 bowl', 147, 70, 'Soak 1 cup sabudana (sago) in water overnight and drain well; Heat 2 tbsp ghee, add 1 tsp cumin seeds, 2 chopped green chilies, and 1 boiled chopped potato; Sauté for 23 minutes; Add soaked sabudana, salt, and 1/4 cup roasted peanuts; Cook on low flame for 56 minutes until pearls turn translucent; Add lemon juice and chopped coriander; Serve hot with curd.', 'ChatGPT', '044.jpg', 'dinner');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Tomato Soup', 'Veg', 'Continental', 'milk, tomato, sugar, chili powder, cloves', '1 glass', 101, 65, 'Heat 1 tbsp butter in a pan, sauté 1 chopped onion and 2 chopped garlic cloves; Add 4 chopped tomatoes and cook until soft; Blend the mixture into a smooth puree; Strain and return to heat; Add salt, pepper, 1 tsp sugar, and a pinch of chili flakes; Simmer for 56 minutes; Add 1 tbsp cream and stir; Serve hot with croutons or toast.', 'ChatGPT', '045.jpg', 'breakfast');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Green Tea', 'Veg', 'Asian', 'green tea leaves, water, lemon, honey ', '1 cup', 30, 10, ' Boil 1 cup of water; Add 1 tsp green tea leaves and steep for 2-3 minutes; Strain the tea; Add honey and lemon juice as desired; Serve hot.', 'ChatGPT', '046.jpg', 'beverages');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chocolate Shake', 'Veg', 'Western', 'garlic, ghee, paneer, ginger, onion', '1 glass', 225, 48, 'Add 2 cups chilled milk, 3 tbsp cocoa powder, 2 tbsp sugar, and 1/4 cup chocolate syrup to a blender; Blend until smooth and frothy; Add 1 scoop of vanilla or chocolate ice cream and blend again briefly; Pour into a tall glass and drizzle extra chocolate syrup on top; Garnish with chocolate shavings or sprinkles; Serve chilled with a straw.', 'ChatGPT', '047.jpg', 'shakes');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Paneer Roll', 'Veg', 'North Indian', 'onion, cloves, tomato, paneer, rice', '1 piece', 168, 46, 'Heat 1 tbsp oil in a pan, sauté 1 chopped onion, 1 chopped capsicum, and 1 tsp ginger-garlic paste; Add 1 cup paneer cubes, 1/2 tsp chili powder, 1/2 tsp garam masala, salt, and 2 tbsp tomato ketchup; Stir-fry for 45 minutes and set aside; Warm a chapati or paratha, spread green chutney, place the paneer mixture in the center, and roll tightly; Wrap in foil or butter paper; Serve warm with extra chutney or sauce on the side.', 'ChatGPT', '048.jpg', 'street food');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Veg Biryani', 'Veg', 'Hyderabadi', 'sugar, ghee, lemon, cardamom, pasta', '1 bowl', 203, 21, 'Soak 1.5 cups basmati rice for 30 minutes and cook until 90% done; In a large pan, heat 2 tbsp oil and 1 tbsp ghee, add whole spices (bay leaf, cloves, cardamom, cinnamon), 1 chopped onion, 1 tsp ginger-garlic paste, and sauté until golden; Add 1 cup chopped vegetables (carrot, beans, peas), 1/2 tsp turmeric, 1 tsp biryani masala, and salt; Cook for 56 minutes, add 1/2 cup curd, and simmer until veggies are tender; Layer the cooked rice over the vegetable mixture, sprinkle saffron milk and fried onions; Cover and cook on low flame for 1012 minutes; Fluff gently and serve hot with raita or salad.', 'ChatGPT', '049.jpg', 'snack');
INSERT INTO recipes (name, type, region, ingredients, quantity, avg_cost, time_needed, steps, recipe_by, image, category) VALUES ('Chicken Sandwich', 'Non-Veg', 'Western', 'chicken, garlic, turmeric, paneer, cardamom', '2 piece', 57, 60, 'Boil and shred 1 chicken breast; In a bowl, mix shredded chicken, 2 tbsp mayonnaise, 1 tsp mustard, salt, pepper, and chopped lettuce; Take 2 slices of bread, optionally toast them lightly; Spread the chicken filling between the slices; Cut into halves or triangles; Serve with ketchup, fries, or salad.', 'ChatGPT', '050.jpg', 'lunch');